// src/api.js
async function handleResponse(res) {
    if (!res.ok) {
      const err = await res.text();
      throw new Error(`${res.status}: ${err}`);
    }
    return res.json();
  }

  // UE Management
  export function addUEs(uids) {
    return fetch('/api/ue/add', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ uids }),
    }).then(handleResponse);
  }

  export function removeUEs(uids) {
    return fetch('/api/ue/remove', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ uids }),
    }).then(handleResponse);
  }

  export function listUEs() {
    return fetch('/api/ue/list').then(handleResponse);
  }

  // Orchestrator actions
  export function handover(ue_id, target_vbbu) {
    return fetch('/api/handover', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ue_id, target_vbbu }),
    }).then(handleResponse);
  }

  export function migrate(source_vbbu, deactivate = false) {
    return fetch('/api/migrate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ source_vbbu, deactivate }),
    }).then(handleResponse);
  }

  export function activateVBBU(vbbu) {
    return fetch('/api/vbbu/activate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ vbbu }),
    }).then(handleResponse);
  }

  export function deactivateVBBU(vbbu) {
    return fetch('/api/vbbu/deactivate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ vbbu }),
    }).then(handleResponse);
  }

  // Monitoring endpoints
  export function getAssignments() {
    return fetch('/api/assignments').then(handleResponse);
  }

  export function getLoads() {
    return fetch('/api/loads').then(handleResponse);
  }

  export function getVBBUs() {
    return fetch('/api/vbbus').then(handleResponse);
  }
